insert into martyrs (fid, photo, name, hometown, gender, politics_status, birth_date, dept, position, achievement, death_date, death_campaign, death_address, bury_point, deeds) values (0, '/upload/file/2103/487aed559a8a41e4801b5a04544ce567.jpg', '陈有源', '四川省达州市', 1, '不详', '1930', '志愿军444团2营5连', '战士', '不详', '1951-01', '在抗美援朝战争中牺牲', '1951年1月29日在抗美援朝战争中牺牲', '开江县新宁镇万花岭村2组', '陈有源，男，汉族，生于1930年，四川省达州市开心就好新宁镇人，于1950年1月参军入伍，生前系志愿军444团2营5连战士。于1951年1月29日在抗美援朝战争中牺牲。时年21岁。1951年9月1日志愿军444团政治处批准为烈士。');
insert into martyrs (fid, photo, name, hometown, gender, politics_status, birth_date, dept, position, achievement, death_date, death_campaign, death_address, bury_point, deeds) values (0, '/upload/file/2103/c651329ec1b74fe79e1030e73a5e7511.jpg', '陈力', '四川省达州市', 1, '不详', '1981-12', '开江县交警大队', '二级警员', '不详', '2003-09', '在勘验事故现场中为保护战友的生命壮烈牺牲', '四川省开江县拔妙乡', '开江县新宁镇万花岭村2组', '陈力，男，汉族，1981年12月21日出生，1998年1月1日加入共青团，2000年7月毕业于四川泸州警官学院，2000年9月至2001年8月在开江县公安局西城派出所见习，2001年9月考录并安排在开江县公安交警大队工作，2003年9月11日在勘验事故现场中为保护战友的生命壮烈牺牲。2003年9月12日被四川省公安厅追授为二级警员警衔，2003年11月24日被四川省公安厅追记为一等功臣，2004年5月30日被中共四川省委追认为中国共产党党员，2004年9月30日被四川省人民政府批准为革命烈士。');
