insert into martyrs (fid, photo, name, hometown, gender, politics_status, birth_date, dept, position, achievement, death_date, death_campaign, death_address, bury_point, deeds) values (0, '/upload/file/2103/a92f76811acd4c1abc827af7d2fd2b4e.jpg', '谭奇权', '四川省达州市', 1, '不详', '1920', '陕北军工厂', '干部', '不详', '1942', '1942年在陕北战斗中失踪', '1942年在陕北战斗中失踪', '开江县新宁镇万花岭村2组', '谭奇权，男，汉族，四川省达州市开江县永兴镇人，生于1920年，1938年8月参加革命工作，生前系陕北军工厂干部，1942年在陕北战斗中失踪，时年22岁。开江县人民政府1982年1月5日批准为烈士。');
insert into martyrs (fid, photo, name, hometown, gender, politics_status, birth_date, dept, position, achievement, death_date, death_campaign, death_address, bury_point, deeds) values (0, '/upload/file/2103/4f19ef17dad24355a812b33de8415bc8.jpg', '覃文', '四川省达州市', 1, '不详', '1908-10', '中共四川省委宣传部', '部长', '不详', '1933', '1933年9月15日，被叛徒出卖，为保全战友，10月被敌杀害', '1933年在成都市被捕后牺牲', '开江县新宁镇万花岭村2组', '覃文，男，汉族，四川省达州市开江县灵岩镇人，中共党员，1908年10月16日出生于四川省开江县太和乡一个中等地主家庭。他从小耳闻目睹军阀、土匪肆虐，立志长大后为民除害。1926年，他到上海求学。此时正值大革命高潮时期，革命风暴席卷全国，上海的革命声势更为浩大。覃文目睹了上海工人的三次武装起义，深为无产阶级的忘我牺牲精神所感动。他毅然放弃在中学念书的机会，边打工边参加革命活动。1927年冬，他加入了中国共产党，曾任中共四川省委宣传部部长，1933年9月15日，被叛徒出卖，为保全战友，10月被敌杀害，时年25岁。开江县人民政府民政科1950年8月凭证注册为烈士。');
