insert into martyrs (fid, photo, name, hometown, gender, politics_status, birth_date, dept, position, achievement, death_date, death_campaign, death_address, bury_point, deeds) values (0, '/upload/file/2109/c51b962cd31543fc9de86d9ccb0f7a0b.jpg', '刘先慎', '四川省达州市', 1, '中国共产党员', '1904-09', '中共大竹县特支委员会', '委员', '不详', '1940-09', '1940年5月上旬，由于叛徒出卖，不幸被捕，同年9月病逝。', '大竹县', '大竹烈士陵园', '刘先慎（1904—1940），大竹县竹阳镇人，1927年加入中国共产党，1938年后相继任中共大竹特支组织、中心县委组织部长。参与领导大竹抗日救亡运动，任抗敌剧团团长、七七歌咏队队长。1940年5月被捕，同年9月病逝。');
