drop database if exists mmn;

create database mmn default charset utf8mb4;

use mmn;

create table users (
    `uid` integer unsigned primary key auto_increment comment '唯一 id 标识',
    `uname` varchar(40) unique not null comment '用户名',
    `passwd` tinytext not null comment '用户密码',
    `type` tinyint unsigned  default 1 comment '用户类型',     -- 1 普通用户    2 家属用户    3 管理员
    `ctime` datetime not null default now() comment '用户注册时间',
    `mtime` datetime not null default now() comment '修改用户信息时间'
) comment '用户信息表';

create table records (
    `rid` integer unsigned primary key auto_increment comment '唯一 id 标识',
    `uid` integer unsigned not null comment '用户唯一 id 标识',
    `mid` integer unsigned not null comment '烈士唯一 id 标识',
    `candle` tinyint unsigned default 0 comment '是否点燃蜡烛', -- 0 未点燃    1 点燃
    `flower` tinyint unsigned comment '献花朵数',
    `message` text comment '留言',
    `ctime` datetime not null default now() comment '纪念时间'
) comment '用户纪念记录表';

create table cities (
    `cid` integer unsigned primary key auto_increment comment '唯一 id 标识',
    `name` varchar(20) unique not null comment '城市名称',
    `ctime` datetime not null default now() comment '城市信息记录时间',
    `mtime` datetime not null default now() comment '城市信息修改时间'
) comment '城市信息表';


create table facilities (
    `fid` integer unsigned primary key auto_increment comment '唯一 id 标识',
    `cid` integer unsigned not null comment '所属城市 id', -- 纪念设施信息收录在哪个城市
    `name` tinytext not null comment '设施名称',
    `img` tinytext not null comment '设施图片',
    `intro` text comment '介绍',
    `phone` tinytext not null comment '联系方式',
    `address` tinytext not null comment '设施地址',
    `hint` tinytext comment '乘车提示',
    `ctime` datetime not null default now() comment '登记时间',
    `mtime` datetime not null default now() comment '修改时间'
) comment '纪念设施表';


create table martyrs (
    `mid` integer unsigned primary key auto_increment comment '唯一 id 标识',
    `fid` integer unsigned not null comment '烈士所属烈士纪念设施 id', -- 烈士信息收录在哪个纪念设施
    `photo` tinytext not null comment '烈士碑像',
    `name` tinytext not null comment '姓名',
    `hometown` tinytext not null comment '籍贯',
    `gender` tinyint unsigned not null comment '性别', -- 1 男    2 女
    `politics_status` tinytext comment '政治面貌',
    `birth_date` tinytext comment '出生日期', -- 因为日期不完整，所以不能使用 date 存储
    `dept` tinytext comment '生前单位',
    `position` tinytext comment '生前职位',
    `achievement` tinytext comment '立功情况',
    `death_date` tinytext comment '牺牲日期', -- 因为日期不完整，所以不能使用 date 存储
    `death_campaign` text comment '牺牲战役',
    `death_address` tinytext comment '牺牲地址',
    `bury_point` tinytext comment '安葬地点',
    `deeds` text comment '烈士事迹',
    `ctime` datetime not null default now() comment '烈士信息记录时间',
    `mtime` datetime not null default now() comment '烈士信息修改时间'
) comment '英烈信息表';


create table logs (
    `lid` integer unsigned primary key auto_increment comment '唯一 id 标识',
    `log` text not null comment '提交的日志',
    `time` datetime not null default now() comment '日志提交时间'
) comment '日志记录表';

